	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setRazonSocial($this->razon_social);

		$copyObj->setTipoDocumentoId($this->tipo_documento_id);

		$copyObj->setNroDocumento($this->nro_documento);

