	public function setTipoComprobante(TipoComprobante $v = null)
	{
		if ($v === null) {
			$this->setTipoComprobanteId(NULL);
		} else {
			$this->setTipoComprobanteId($v->getId());
		}

		$this->aTipoComprobante = $v;

		// Add binding for other direction of this n:n relationship.
		// If this object has already been added to the TipoComprobante object, it will not be re-added.
		if ($v !== null) {
			$v->addComprobante($this);
		}

		return $this;
	}


