	 * @param      PropelPDO Optional Connection object.
	 * @return     TipoComprobante The associated TipoComprobante object.
	 * @throws     PropelException
	 */
	public function getTipoComprobante(PropelPDO $con = null)
	{
		if ($this->aTipoComprobante === null && ($this->tipo_comprobante_id !== null)) {
			$c = new Criteria(TipoComprobantePeer::DATABASE_NAME);
			$c->add(TipoComprobantePeer::ID, $this->tipo_comprobante_id);
			$this->aTipoComprobante = TipoComprobantePeer::doSelectOne($c, $con);
			/* The following can be used additionally to
			   guarantee the related object contains a reference
			   to this object.  This level of coupling may, however, be
			   undesirable since it could result in an only partially populated collection
			   in the referenced object.
			   $this->aTipoComprobante->addComprobantes($this);
			 */
		}
		return $this->aTipoComprobante;
	}

